mapper.select:
SELECT U.IDX AS userIdx, U.ID AS id, U.NICKNAME AS nickname, U.EMAIL AS email, U.USER_TYPE_IDX AS userType, U.STATUS AS status, DATE_FORMAT(U.CREATE_AT, '%Y-%m-%d %H:%i:%s') as createdAt, S.SHELTER_NAME AS sheltername FROM USER U LEFT JOIN SHELTERS S ON U.IDX = S.USER_IDX
AND U.ID LIKE CONCAT('%", #{keyword}, '%')
AND U.NICKNAME LIKE CONCAT('%', #{keyword}, '%')
AND U.EMAIL LIKE CONCAT('%', #{keyword}, '%')
AND U.USER_TYPE_IDX = #{userType}
AND U.STATUS = #{status}
ORDER BY U.IDX DESC LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM USERS U LEFT JOIN SHELTERS S ON U.IDX = S.USER_IDX
AND U.ID LIKE CONCAT('%", #{keyword}, '%')
AND U.NICKNAME LIKE CONCAT('%', #{keyword}, '%')
AND U.EMAIL LIKE CONCAT('%', #{keyword}, '%')
AND U.USER_TYPE_IDX = #{userType}
AND U.STATUS = #{status}
