mapper.update:
UPDATE
	DONATION_COMMENTS
SET
	CONTENT = #{content},
	CREATED_AT = NOW()
WHERE
	IDX = #{idx}
	AND USER_IDX = #{userIdx}
	AND DONATION_IDX = #{donationIdx}

mapper.delete:
DELETE
FROM
	DONATION_COMMENTS
WHERE
	IDX = #{idx}
	AND USER_IDX = #{userIdx}

mapper.update:
UPDATE 
	USERS
SET
  POINT = POINT - #{donatedAmount}
WHERE
  IDX = #{userIdx}
