	IDX = #{reviewIdx}
AND 
	USER_IDX = #{userIdx}

UPDATE
	BOARDS B
SET
	TITLE = #{title},
	CONTENT = #{content}
WHERE
	IDX = #{idx}
	AND B.USER_IDX = #{userIdx}
	AND B.BOARD_TYPE_IDX = 3
