mapper.select:
SELECT S.USER_IDX AS 'IDX', S.SHELTER_NAME, S.SHELTER_ADDRESS, ST.NAME AS 'SHELTER_TYPE' FROM SHELTERS S JOIN SHELTER_TYPES ST ON S.SHELTER_TYPE_IDX = ST.IDX
AND S.SHELTER_ADDRESS LIKE '%${shelterAddress}%'
AND ST.NAME = #{shelterType}
AND S.SHELTER_NAME LIKE '%${shelterName}%'
ORDER BY S.USER_IDX DESC LIMIT #{listSize} OFFSET #{cp}

SELECT A.IDX, S.USER_IDX, A.NAME, A.GENDER, A.AGE, A.`SIZE`, A.NEUTER, A.CREATED_AT, AB.NAME AS 'BREED', AT2.NAME AS 'TYPE', AP.NAME AS 'PERSONALITY', AS2.NAME AS 'ADOPTION_STATUS' FROM ANIMALS A JOIN ANIMAL_BREEDS AB ON A.ANIMAL_BREED_IDX = AB.IDX JOIN ANIMAL_TYPES AT2 ON AB.ANIMAL_TYPE_IDX = AT2.IDX JOIN ANIMAL_PERSONALITIES AP ON A.ANIMAL_PERSONALITY_IDX = AP.IDX JOIN ADOPTION_STATUS AS2 ON A.ADOPTION_STATUS_IDX = AS2.IDX JOIN SHELTERS S ON A.USER_IDX =S.USER_IDX
AND S.USER_IDX = #{idx}
AND AT2.NAME = #{type}
AND AB.NAME = #{breed}
AND A.GENDER = #{gender}
AND A.NEUTER = #{neuter}
AND A.AGE = #{age}
AND AS2.NAME = #{adoptionStatus}
AND AP.NAME = #{personality}
AND A.`SIZE` = #{size}
AND A.NAME LIKE '%${name}%'
ORDER BY IDX DESC LIMIT ${listSize} OFFSET ${cp}
